mapper.select:
	SELECT COUNT(1) 
	  FROM forum.board_like 
	 WHERE board_seq = #{boardSeq}
	   AND board_type_seq = #{boardTypeSeq}
       AND member_seq = #{memberSeq}

	SELECT COUNT(1) 
	  FROM forum.board_dislike 
	 WHERE board_seq = #{boardSeq}
	   AND board_type_seq = #{boardTypeSeq}
       AND member_seq = #{memberSeq}

mapper.update:
	UPDATE forum.board
	   SET title= #{title}, content= #{content}, reg_member_seq=73, update_dtm= DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
 	 WHERE board_seq= #{boardSeq} AND board_type_seq= #{boardTypeSeq}
